mapper.select:
select *
from
    offMeetingMem
where memberIdx = #{findMemberIdx}
and offMeetingIdx = #{offMeetingIdx}
and status = 'ACTIVE'

mapper.update:
update offMeeting set
    headcount = headcount + 1
where offMeetingIdx = #{offMeetingIdx}

mapper.delete:
delete offMeetingMem
where memberIdx = #{findMemberIdx}
and offMeetingIdx = #{offMeetingIdx}
and status = 'ACTIVE'

mapper.update:
update offMeeting set
headcount = headcount - 1
where offMeetingIdx = #{offMeetingIdx}
