mapper.select:
select *
from productReview
where productIdx = #{productIdx}
and productReviewIdx = #{reviewIdx}
and status = 'ACTIVE'

mapper.update:
update productReview set
status = 'DELETED'
where productIdx = #{productIdx}
and productReviewIdx = #{reviewIdx}
and memberIdx = #{memberIdx}
and status = 'ACTIVE'
